/** 
 * @author wind57
 */
class ConfigMapConfigPropertiesTests {
  /** 
 * <pre> spring: cloud: kubernetes: config: name: config-map-a namespace: spring-k8s </pre> a config as above will result in a NormalizedSource where prefix is empty
 */
  @Test void testUseNameAsPrefixUnsetEmptySources(){
    ConfigMapConfigProperties properties=new ConfigMapConfigProperties(true,List.of(),List.of(),Map.of(),true,"config-map-a","spring-k8s",false,false,false,RetryProperties.DEFAULT);
    List<NormalizedSource> sources=properties.determineSources(new MockEnvironment());
    Assertions.assertEquals(sources.size(),1,"empty sources must generate a List with a single NormalizedSource");
    Assertions.assertSame(((NamedConfigMapNormalizedSource)sources.get(0)).prefix(),ConfigUtils.Prefix.DEFAULT,"empty sources must generate a List with a single NormalizedSource, where prefix is empty");
  }
  /** 
 * <pre> spring: cloud: kubernetes: config: useNameAsPrefix: true name: config-map-a namespace: spring-k8s </pre> a config as above will result in a NormalizedSource where prefix is empty, even if "useNameAsPrefix: true", because sources are empty
 */
  @Test void testUseNameAsPrefixSetEmptySources(){
    ConfigMapConfigProperties properties=new ConfigMapConfigProperties(true,List.of(),List.of(),Map.of(),true,"config-map-a","spring-k8s",true,false,false,RetryProperties.DEFAULT);
    List<NormalizedSource> sources=properties.determineSources(new MockEnvironment());
    Assertions.assertEquals(sources.size(),1,"empty sources must generate a List with a single NormalizedSource");
    Assertions.assertSame(((NamedConfigMapNormalizedSource)sources.get(0)).prefix(),ConfigUtils.Prefix.DEFAULT,"empty sources must generate a List with a single NormalizedSource, where prefix is empty," + "no matter of 'spring.cloud.kubernetes.config.useNameAsPrefix' value");
  }
  /** 
 * <pre> spring: cloud: kubernetes: config: useNameAsPrefix: true namespace: spring-k8s sources: - name: config-map-one </pre> a config as above will result in a NormalizedSource where prefix will be equal to the config map name
 */
  @Test void testUseNameAsPrefixUnsetNonEmptySources(){
    ConfigMapConfigProperties.Source one=new ConfigMapConfigProperties.Source("config-map-one",null,Collections.emptyMap(),null,null,null);
    ConfigMapConfigProperties properties=new ConfigMapConfigProperties(true,List.of(),List.of(one),Map.of(),true,"config-map-a","spring-k8s",true,false,false,RetryProperties.DEFAULT);
    List<NormalizedSource> sources=properties.determineSources(new MockEnvironment());
    Assertions.assertEquals(sources.size(),1,"a single NormalizedSource is expected");
    Assertions.assertEquals(((NamedConfigMapNormalizedSource)sources.get(0)).prefix().prefixProvider().get(),"config-map-one");
  }
  /** 
 * <pre> spring: cloud: kubernetes: config: useNameAsPrefix: true namespace: spring-k8s sources: - name: config-map-one useNameAsPrefix: false - name: config-map-two useNameAsPrefix: true - name: config-map-three </pre> this test proves that 'spring.cloud.kubernetes.config.sources[].useNameAsPrefix' will override 'spring.cloud.kubernetes.config.useNameAsPrefix'. For the last entry in sources, since there is no explicit 'useNameAsPrefix', the one from 'spring.cloud.kubernetes.config.useNameAsPrefix' will be taken.
 */
  @Test void testUseNameAsPrefixSetNonEmptySources(){
    ConfigMapConfigProperties.Source one=new ConfigMapConfigProperties.Source("config-map-one",null,Collections.emptyMap(),null,false,null);
    ConfigMapConfigProperties.Source two=new ConfigMapConfigProperties.Source("config-map-two",null,Collections.emptyMap(),null,true,null);
    ConfigMapConfigProperties.Source three=new ConfigMapConfigProperties.Source("config-map-three",null,Collections.emptyMap(),null,true,null);
    ConfigMapConfigProperties properties=new ConfigMapConfigProperties(true,List.of(),List.of(one,two,three),Map.of(),true,"config-map-a","spring-k8s",true,false,false,RetryProperties.DEFAULT);
    List<NormalizedSource> sources=properties.determineSources(new MockEnvironment());
    Assertions.assertEquals(sources.size(),3,"3 NormalizedSources are expected");
    Assertions.assertSame(((NamedConfigMapNormalizedSource)sources.get(0)).prefix(),ConfigUtils.Prefix.DEFAULT);
    Assertions.assertEquals(((NamedConfigMapNormalizedSource)sources.get(1)).prefix().prefixProvider().get(),"config-map-two");
    Assertions.assertEquals(((NamedConfigMapNormalizedSource)sources.get(2)).prefix().prefixProvider().get(),"config-map-three");
  }
  /** 
 * <pre> spring: cloud: kubernetes: config: useNameAsPrefix: false namespace: spring-k8s sources: - name: config-map-one useNameAsPrefix: false explicitPrefix: one - name: config-map-two useNameAsPrefix: true explicitPrefix: two - name: config-map-three explicitPrefix: three - name: config-map-four </pre>
 */
  @Test void testMultipleCases(){
    ConfigMapConfigProperties.Source one=new ConfigMapConfigProperties.Source("config-map-one",null,Collections.emptyMap(),"one",false,null);
    ConfigMapConfigProperties.Source two=new ConfigMapConfigProperties.Source("config-map-two",null,Collections.emptyMap(),"two",true,null);
    ConfigMapConfigProperties.Source three=new ConfigMapConfigProperties.Source("config-map-three",null,Collections.emptyMap(),"three",false,null);
    ConfigMapConfigProperties.Source four=new ConfigMapConfigProperties.Source(null,"config-map-four",Collections.emptyMap(),null,false,null);
    ConfigMapConfigProperties properties=new ConfigMapConfigProperties(true,List.of(),List.of(one,two,three,four),Map.of(),true,"config-map-a","spring-k8s",true,false,false,RetryProperties.DEFAULT);
    List<NormalizedSource> sources=properties.determineSources(new MockEnvironment());
    Assertions.assertEquals(sources.size(),4,"4 NormalizedSources are expected");
    Assertions.assertEquals(((NamedConfigMapNormalizedSource)sources.get(0)).prefix().prefixProvider().get(),"one");
    Assertions.assertEquals(((NamedConfigMapNormalizedSource)sources.get(1)).prefix().prefixProvider().get(),"two");
    Assertions.assertEquals(((NamedConfigMapNormalizedSource)sources.get(2)).prefix().prefixProvider().get(),"three");
    Assertions.assertSame(((NamedConfigMapNormalizedSource)sources.get(3)).prefix(),ConfigUtils.Prefix.DEFAULT);
  }
  /** 
