/** 
 * @author wind57
 */
class KubernetesConfigDataLocationResolverTests {
  private static final DeferredLogFactory FACTORY=Supplier::get;
  private static final KubernetesConfigDataLocationResolver NOOP_RESOLVER=new KubernetesConfigDataLocationResolver(FACTORY){
    @Override protected void registerBeans(    ConfigDataLocationResolverContext resolverContext,    ConfigDataLocation location,    Profiles profiles,    PropertyHolder propertyHolder,    KubernetesNamespaceProvider namespaceProvider){
    }
  }
;
  private static final ConfigDataLocationResolverContext RESOLVER_CONTEXT=Mockito.mock(ConfigDataLocationResolverContext.class);
  @Test void testGetPrefix(){
    Assertions.assertEquals("kubernetes:",NOOP_RESOLVER.getPrefix());
  }
  /** 
 * method returns true via 'KUBERNETES.isEnforced(context.getBinder())'
 */
  @Test void testIsResolvableTrue(){
    MockEnvironment environment=new MockEnvironment();
    environment.setProperty("spring.main.cloud-platform","KUBERNETES");
    ConfigurationPropertySources.attach(environment);
    Binder binder=new Binder(ConfigurationPropertySources.get(environment));
    Mockito.when(RESOLVER_CONTEXT.getBinder()).thenReturn(binder);
    ConfigDataLocation configDataLocation=ConfigDataLocation.of("kubernetes:abc");
    boolean result=NOOP_RESOLVER.isResolvable(RESOLVER_CONTEXT,configDataLocation);
    Assertions.assertTrue(result);
  }
  @Test void testIsResolvableFalse(){
    MockEnvironment environment=new MockEnvironment();
    ConfigurationPropertySources.attach(environment);
    Binder binder=new Binder(ConfigurationPropertySources.get(environment));
    Mockito.when(RESOLVER_CONTEXT.getBinder()).thenReturn(binder);
    ConfigDataLocation configDataLocation=ConfigDataLocation.of("kubernetes:abc");
    boolean result=NOOP_RESOLVER.isResolvable(RESOLVER_CONTEXT,configDataLocation);
    Assertions.assertFalse(result);
  }
  @Test void testResolve(){
    ConfigDataLocation configDataLocation=ConfigDataLocation.of("kubernetes:abc");
    List<KubernetesConfigDataResource> result=NOOP_RESOLVER.resolve(RESOLVER_CONTEXT,configDataLocation);
    Assertions.assertEquals(0,result.size());
  }
  /** 
 * <pre> a test that only looks at 3 properties: - application name - namespace (via 'spring.cloud.kubernetes.client.namespace') - KubernetesClientProperties (created via bindOrCreate) </pre>
 */
  @Test void testResolveProfileSpecificOne(){
    MockEnvironment environment=new MockEnvironment();
    environment.setProperty("spring.application.name","k8s-app-name");
    environment.setProperty("spring.cloud.kubernetes.client.namespace","non-default-namespace");
    ConfigurationPropertySources.attach(environment);
    Binder binder=new Binder(ConfigurationPropertySources.get(environment));
    Mockito.when(RESOLVER_CONTEXT.getBinder()).thenReturn(binder);
    Mockito.when(RESOLVER_CONTEXT.getBootstrapContext()).thenReturn(new DefaultBootstrapContext());
    Profiles profiles=Mockito.mock(Profiles.class);
    ConfigDataLocation configDataLocation=ConfigDataLocation.of("kubernetes:abc");
    List<KubernetesConfigDataResource> result=NOOP_RESOLVER.resolveProfileSpecific(RESOLVER_CONTEXT,configDataLocation,profiles);
    Assertions.assertEquals(1,result.size());
    Assertions.assertEquals("k8s-app-name",result.get(0).getEnvironment().getRequiredProperty("spring.application.name"));
    Assertions.assertEquals("non-default-namespace",result.get(0).getEnvironment().getRequiredProperty("spring.cloud.kubernetes.client.namespace"));
    Assertions.assertEquals("Spring-Cloud-Kubernetes-Application",result.get(0).getProperties().userAgent());
    Assertions.assertEquals("non-default-namespace",result.get(0).getProperties().namespace());
  }
  /** 
