@Configuration(proxyBeanMethods=false) @ConditionalOnSpringCloudKubernetesReactiveDiscovery @EnableConfigurationProperties({DiscoveryClientHealthIndicatorProperties.class,KubernetesDiscoveryProperties.class}) class KubernetesDiscoveryClientReactiveAutoConfiguration {
  @Bean @ConditionalOnMissingBean WebClient.Builder webClientBuilder(){
    return WebClient.builder();
  }
  @Bean @ConditionalOnMissingBean KubernetesReactiveDiscoveryClient kubernetesReactiveDiscoveryClient(  WebClient.Builder webClientBuilder,  KubernetesDiscoveryProperties properties){
    return new KubernetesReactiveDiscoveryClient(webClientBuilder,properties);
  }
  @Bean @ConditionalOnMissingBean PodUtils<?> kubernetesDiscoveryPodUtils(){
    return new KubernetesDiscoveryPodUtils();
  }
  /** 
 * Post an event so that health indicator is initialized.
 */
  @Bean @ConditionalOnSpringCloudKubernetesReactiveDiscoveryHealthInitializer KubernetesDiscoveryClientHealthIndicatorInitializer reactiveIndicatorInitializer(  ApplicationEventPublisher applicationEventPublisher,  PodUtils<?> podUtils){
    return new KubernetesDiscoveryClientHealthIndicatorInitializer(podUtils,applicationEventPublisher);
  }
  /** 
 * unlike the blocking implementation, we need to register the health indicator.
 */
  @Bean @ConditionalOnSpringCloudKubernetesReactiveDiscoveryHealthInitializer ReactiveDiscoveryClientHealthIndicator kubernetesReactiveDiscoveryClientHealthIndicator(  KubernetesReactiveDiscoveryClient client,  DiscoveryClientHealthIndicatorProperties properties){
    return new ReactiveDiscoveryClientHealthIndicator(client,properties);
  }
}

