@Configuration(proxyBeanMethods=false) public static class Servlet {
    @Bean @ConditionalOnMissingClass("org.springframework.web.reactive.function.client.WebClient") @ConditionalOnMissingBean(RestTemplate.class) public RestTemplate restTemplate(){
      return new RestTemplateBuilder().build();
