    @Bean @ConditionalOnMissingClass("org.springframework.web.reactive.function.client.WebClient") public DiscoveryClient kubernetesDiscoveryClient(    RestTemplate restTemplate,    KubernetesDiscoveryClientProperties properties){
      return new KubernetesDiscoveryClient(restTemplate,properties);
    }
    @Bean @ConditionalOnClass({HealthIndicator.class}) @ConditionalOnDiscoveryHealthIndicatorEnabled public InitializingBean indicatorInitializer(    ApplicationEventPublisher applicationEventPublisher,    ApplicationContext applicationContext){
      return () -> applicationEventPublisher.publishEvent(new InstanceRegisteredEvent<>(applicationContext.getId(),null));
    }
  }
@Configuration(proxyBeanMethods=false) @ConditionalOnReactiveDiscoveryEnabled public static class Reactive {
    @Bean @ConditionalOnClass(name={"org.springframework.web.reactive.function.client.WebClient"}) @ConditionalOnMissingBean(WebClient.Builder.class) public WebClient.Builder webClientBuilder(){
      return WebClient.builder();
    }
    @Bean @ConditionalOnClass(name={"org.springframework.web.reactive.function.client.WebClient"}) public ReactiveDiscoveryClient kubernetesReactiveDiscoveryClient(    WebClient.Builder webClientBuilder,    KubernetesDiscoveryClientProperties properties){
