/** 
 * @author Ryan Baxter
 */
class KubernetesDiscoveryClientTests {
  private static final String APPS=null;
  private static final String APPS_NAME=null;
  private static WireMockServer wireMockServer;
  @BeforeAll static void beforeAll(){
    wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor(wireMockServer.port());
    stubFor(get("/apps").willReturn(aResponse().withStatus(200).withBody(APPS).withHeader("content-type","application/json")));
    stubFor(get("/apps/test-svc-3").willReturn(aResponse().withStatus(200).withBody(APPS_NAME).withHeader("content-type","application/json")));
    stubFor(get("/apps/does-not-exist").willReturn(aResponse().withStatus(200).withBody("").withHeader("content-type","application/json")));
  }
  @Test void getInstances(){
    RestTemplate rest=new RestTemplateBuilder().build();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,null,Set.of(),Map.of(),null,KubernetesDiscoveryProperties.Metadata.DEFAULT,0,false,false,wireMockServer.baseUrl());
    KubernetesDiscoveryClient discoveryClient=new KubernetesDiscoveryClient(rest,properties);
    assertThat(discoveryClient.getServices()).contains("test-svc-1","test-svc-3");
  }
  @Test void getServices(){
    RestTemplate rest=new RestTemplateBuilder().build();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,Set.of(),true,60,false,null,Set.of(),Map.of(),null,KubernetesDiscoveryProperties.Metadata.DEFAULT,0,false,false,wireMockServer.baseUrl());
    KubernetesDiscoveryClient discoveryClient=new KubernetesDiscoveryClient(rest,properties);
    Map<String,String> metadata=new HashMap<>();
    metadata.put("spring","true");
    metadata.put("http","8080");
    metadata.put("k8s","true");
    assertThat(discoveryClient.getInstances("test-svc-3")).contains(new DefaultKubernetesServiceInstance("uid2","test-svc-3","2.2.2.2",8080,metadata,false,"namespace2",null,null));
    assertThat(discoveryClient.getInstances("does-not-exist")).isEmpty();
  }
  @ParameterizedTest @MethodSource("servicesFilteredByNamespacesSource") void getServicesFilteredByNamespaces(  Set<String> namespaces,  List<String> expectedServices){
    RestTemplate rest=new RestTemplateBuilder().build();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,namespaces,true,60,false,null,Set.of(),Map.of(),null,KubernetesDiscoveryProperties.Metadata.DEFAULT,0,false,false,wireMockServer.baseUrl());
    KubernetesDiscoveryClient discoveryClient=new KubernetesDiscoveryClient(rest,properties);
    assertThat(discoveryClient.getServices()).containsExactlyInAnyOrderElementsOf(expectedServices);
  }
  @ParameterizedTest @MethodSource("instancesFilteredByNamespacesSource") void getInstancesFilteredByNamespaces(  Set<String> namespaces,  String serviceId,  List<String> expectedInstances){
    RestTemplate rest=new RestTemplateBuilder().build();
    KubernetesDiscoveryProperties properties=new KubernetesDiscoveryProperties(true,true,namespaces,true,60,false,null,Set.of(),Map.of(),null,KubernetesDiscoveryProperties.Metadata.DEFAULT,0,false,false,wireMockServer.baseUrl());
    KubernetesDiscoveryClient discoveryClient=new KubernetesDiscoveryClient(rest,properties);
    assertThat(discoveryClient.getInstances(serviceId)).map(ServiceInstance::getInstanceId).containsExactlyInAnyOrderElementsOf(expectedInstances);
  }
  private static Stream<Arguments> servicesFilteredByNamespacesSource(){
    return Stream.of(Arguments.of(Set.of(),List.of("test-svc-1","test-svc-3")),Arguments.of(Set.of("namespace1","namespace2"),List.of("test-svc-1","test-svc-3")),Arguments.of(Set.of("namespace1"),List.of("test-svc-1")),Arguments.of(Set.of("namespace2","does-not-exist"),List.of("test-svc-3")));
