 * @author Ryan Baxter
 */
class ConfigServerBootstrapperTests {
  private static WireMockServer wireMockServer;
  protected ConfigurableApplicationContext context;
  @AfterEach void close(){
    wireMockServer.stop();
    if (this.context != null) {
      this.context.close();
    }
  }
  @BeforeEach void beforeAll() throws JsonProcessingException {
    wireMockServer=new WireMockServer(options().dynamicPort());
    wireMockServer.start();
    WireMock.configureFor(wireMockServer.port());
    String appsName=null.formatted(wireMockServer.port(),wireMockServer.baseUrl());
    stubFor(get("/apps/spring-cloud-kubernetes-configserver").willReturn(aResponse().withStatus(200).withBody(appsName).withHeader("content-type","application/json")));
    Environment environment=new Environment("test","default");
    Map<String,Object> properties=new HashMap<>();
    properties.put("hello","world");
    PropertySource p=new PropertySource("p1",properties);
    environment.add(p);
    ObjectMapper objectMapper=new ObjectMapper();
    stubFor(get("/application/default").willReturn(aResponse().withStatus(200).withBody(objectMapper.writeValueAsString(environment)).withHeader("content-type","application/json")));
  }
  @AfterEach void afterEach(){
    context.close();
  }
  @Test void testBootstrapper(){
    context=setup().run();
    verify(1,getRequestedFor(urlEqualTo("/apps/spring-cloud-kubernetes-configserver")));
    assertThat(context.getEnvironment().getProperty("hello")).isEqualTo("world");
  }
  SpringApplicationBuilder setup(  String... env){
    SpringApplicationBuilder builder=new SpringApplicationBuilder(TestConfig.class).properties(addDefaultEnv(env));
    builder.addBootstrapRegistryInitializer(new ConfigServerBootstrapper());
    return builder;
  }
  private String[] addDefaultEnv(  String[] env){
    Set<String> set=new HashSet<>();
    if (env != null) {
      set.addAll(List.of(env));
    }
    set.add("server.port=0");
    set.add("spring.cloud.config.discovery.enabled=true");
    set.add("spring.config.import=optional:configserver:");
    set.add("spring.cloud.config.discovery.service-id=spring-cloud-kubernetes-configserver");
    set.add("spring.cloud.kubernetes.discovery.discoveryServerUrl=" + wireMockServer.baseUrl());
    set.add("spring.main.web-application-type=NONE");
    return set.toArray(new String[0]);
  }
@TestConfiguration static class TestConfig {
  }
}

