    contextRunner.withPropertyValues("spring.main.cloud-platform=KUBERNETES","spring.cloud.kubernetes.discovery.discovery-server-url=http://k8sdiscoveryserver").withClassLoader(new FilteredClassLoader("org.springframework.boot.actuate")).run(context -> {
      assertThat(context).hasSingleBean(KubernetesReactiveDiscoveryClient.class);
      assertThat(context).doesNotHaveBean(ReactiveDiscoveryClientHealthIndicator.class);
    }
);
  }
}

